// Foo controller
exports.foo = function(req, res) {
  res.send('This should not be displayed');
};
exports.foo.bind = '/foo';
exports.foo.before = function(req, res, next) {
  res.send('Foo!');
};


// Bar controller
exports.bar = function(req, res) {
  res.send('Bar!');
};
exports.bar.bind = '/bar';
exports.bar.before = [
  function(req, res, next) {
    console.log('First middleware called.');
    next();
  },
  function(req, res, next) {
    console.log('Second middleware called, too.');
    next();
  }
];